import React, { Component } from 'react'

// A section to add general information like name, email, phone number

class General extends Component {
  constructor() {
    super()
    this.state = {}
    this.doSomething = this.doSomething.bind(this)
    this.buildGeneral = this.buildGeneral.bind(this)
  }

  doSomething() {
    const userName = document.getElementById('name').value;
    const userEmail = document.getElementById('email').value;
    const userPhone = document.getElementById('phoneNum').value;
    this.setState(() => {
      return {
        name: userName,
        email: userEmail,
        phone: userPhone
      }
    });
  }

  buildGeneral() {
    if (Object.keys(this.state).length !== 0) {
      return (
        <div>
          <p>Name: {this.state.name}</p>
          <p>Email: {this.state.email}</p>
          <p>Phone: {this.state.phone}</p>
          <button className='edit' onClick={this.editInfo}>Edit</button>
        </div>
      )
    }
    return <p></p>;
  }

  render() {
    return (
      <div>
        <label htmlFor='name'>Name:</label>
        <input type='text' id='name' name='name'></input>
        <label htmlFor='email'>Email:</label>
        <input type='email' id='email' name='email'></input>
        <label htmlFor='phoneNum'>Phone Number:</label>
        <input type='text' id='phoneNum' name='phoneNum'></input>
        <input type='submit' value='Press to Test' onClick={this.doSomething}></input>
        {this.buildGeneral()}
      </div>
    )
  }
}

export default General
