import React, { Component } from 'react'
import Overview from './components/Overview';

class App extends Component {
  constructor() {
    super();
    this.state = {
      task: '',
      tasks: [],
    }
  }
  newTasker(e) {
    this.setState = {
      task: e.target.value,
    }
  }
  addTasker() {
    this.state.tasks.push(this.state.task);
    console.log(this.state.tasks);
  }
  render() {
    return (
      <div>
        <input id='inputText' type='text' onChange={this.newTasker}></input>
        <button onClick={this.addTasker}>Add Task</button>
        <Overview />
      </div>
    );
  }
}

export default App;
